<?php$C = mysqli_connect("localhost","root","","db");if($C == true){    if(isset($_POST['search'])){     $countryname = $_POST['countryName'];     $query= "SELECT * FROM covid_statistics WHERE Country='$countryname'";     $RES = mysqli_query($C,$query);     if(mysqli_num_rows($RES) > 0){        while($data = mysqli_fetch_assoc($RES)){            $COUNTRY = $data['Country'];            $totalcases = $data['Total_Cases'];            $totaldeaths = $data['Total_Deaths'];            $totalrecovered = $data['Total_Recovered'];            $activecases = $data['Active_Cases'];            $seriouscritical = $data['Serious_Critical'];            $totaltests = $data['Total_Tests'];            include_once('country.html');        }     }else{         echo mysqli_error($C);     }}}mysqli_close($C);?>